frontApp.controller("menuCategoryPostController",["$scope","$http","$window","CategoryPost",function(n,t,i,r){n.categories=[];n.posts=[];n.dichvus=[];t.get("/API/CategoriesAPI/").success(function(t){var i=r.getallCategory(t);angular.forEach(i,function(t){t.idCategoryParent==1&&n.categories.push(t)})});t.get("/API/PostsAPI/").success(function(t){angular.forEach(t,function(t){t.idCategory==2&&n.dichvus.push(t)})});t.get("/API/PostsAPI/").success(function(t){angular.forEach(t,function(t){t.idCategory==3&&n.posts.push(t)})})}]);